INSERT INTO `events` (`event_id`, `event_name`, `event_type`, `event_content`, `event_platform`, `individual_or_team`, `event_picture`, `apply_start_time`, `apply_end_time`, `event_start_time`, `maximum_people`, `valid`, `created_at`) VALUES
(1, '跑跑卡丁車RUSH+ 速度盃 Season 1', 'KartRider', 'YouTube跑跑實況主為活化跑跑社群\r\n\r\n舉辦East Asia伺服器私人賽事\r\n\r\n讓玩家除了能夠在賽道上奔馳\r\n\r\n更能讓所有玩家體驗電子競技熱情', 'Mobile', '團隊', '1724227688.jpg', '2022-07-15 00:00:00', '2022-07-31 23:00:00', '2022-08-13 14:00:00', 16, 1, '0000-00-00 00:00:00'),
(2, 'APEX - INTOVOID娛樂賽', 'Apex Legends', 'APEX - INTOVOID娛樂賽 是SDLP Community主辦的第四届娛樂賽。本届娛樂賽開放給各位玩家報名。', 'PC', '團隊', '1724228503.png', '2022-10-07 00:00:00', '2022-11-01 00:00:00', '2022-11-18 20:00:00', 60, 1, '0000-00-00 00:00:00'),
(3, 'G-EIGHT《Just Dance 舞力全開 2023》舞王挑戰賽', 'Just Dance', 'G-EIGHT 遊戲展，一場台灣玩家專屬的遊戲展。\r\n\r\n在這個舞台上，盡情展現出你的律動吧！\r\n\r\n你會是台灣最強舞王嗎？左扭、右擺，右手舉完左邊 WAVE 都能見招拆招？\r\n\r\n馬上報名參賽，所有玩家都在期待你的表現！\r\n\r\n最速報名的前 20 名參賽者，還將在現場報到時獲贈 Ubisoft 特別款 JOYCON 保護套！', 'Switch', '個人', '1724228520.jpg', '2022-11-25 00:00:00', '2022-12-09 00:00:00', '2022-12-10 14:00:00', 32, 1, '0000-00-00 00:00:00'),
(4, '娜迪亞《鬥陣特攻2》冬季盃2023 (Nadiya Cup Winter 2023)', 'Overwatch 2', '❄️Overwatch冬季盃❄️\r\n\r\n🏁EZK GAMING-娜迪亞《鬥陣特攻2》冬季盃2023 (Nadiya Cup Winter 2023)🏁\r\n\r\n⭐參賽對象：台港澳全體Overwatch玩家\r\n\r\n💵獎勵：\r\n\r\n       冠軍-$2,500港幣(約$10,000台幣)+港幣5000元(2000FUN現金券)\r\n\r\n       亞軍-$1,000港幣(約$4,000台幣)+港幣3000元(2000FUN現金券)\r\n\r\n       季軍-$300港幣(約$1,200台幣)+港幣2000元(2000FUN現金券)\r\n\r\n       參賽抽獎：2000FUN現金券港幣$100元(約$400台幣)x5份\r\n\r\n                         Steelseries Rival 3 wireless mouse(價值港幣$599元)x1支\r\n\r\n                         Steelseries Aerox 3 wireless mouse(價值港幣$899元)x2支\r\n\r\n                                       —(參賽抽獎獎勵共八項)\r\n\r\n       觀賽抽獎：2000FUN現金券港幣$100元(約$400台幣)x10份《觀賽抽獎》\r\n\r\n            2000FUN現金券港幣$100元(約$400台幣)x5份《賽前預測遊戲抽獎》\r\n\r\n                         Steelseries Aerox 3 wireless mouse(價值港幣$899元)x3支\r\n\r\n                                                                                 —(觀賽抽獎獎勵共十八項)', 'PC', '團隊', '1724228553.png', '2022-12-14 00:00:00', '2023-01-12 23:45:00', '2023-01-14 14:00:00', 16, 1, '0000-00-00 00:00:00'),
(5, '《Just Dance 舞力全開 2023》TGS娛樂賽', 'Just Dance', '2023台北國際電玩展上最活力四射的電競盛事！\r\n\r\n燃燒你的熱情！燃燒你的靈魂！將你沸騰的能量用熱舞迸射出來！\r\n\r\n來吧！站上這個絢麗的舞台，讓眾人為你的舞技心醉神迷！\r\n\r\n「《Just Dance 舞力全開 2023》TGS娛樂賽」就在「台北國際電玩展」，一起來秀吧！', 'Switch', ' 個人', '1724228591.jpg', '2023-01-13 21:00:00', '2023-01-30 00:00:00', '2023-02-05 15:00:00', 21, 1, '0000-00-00 00:00:00'),
(6, '《Just Dance 舞力全開 2023》TGS娛樂賽【複賽】', 'Just Dance', '2023台北國際電玩展上最活力四射的電競盛事！\r\n\r\n燃燒你的熱情！燃燒你的靈魂！將你沸騰的能量用熱舞迸射出來！\r\n\r\n來吧！站上這個絢麗的舞台，讓眾人為你的舞技心醉神迷！\r\n\r\n「《Just Dance 舞力全開 2023》TGS娛樂賽」就在「台北國際電玩展」，一起來秀吧！', 'Switch', ' 個人', '1724228607.jpg', '2023-02-05 00:00:00', '2023-02-12 00:00:00', '2023-02-19 13:30:00', 21, 1, '0000-00-00 00:00:00'),
(7, '《Just Dance 舞力全開 2023》TGS娛樂賽【決賽】', 'Just Dance', '2023台北國際電玩展上最活力四射的電競盛事！\r\n\r\n燃燒你的熱情！燃燒你的靈魂！將你沸騰的能量用熱舞迸射出來！\r\n\r\n來吧！站上這個絢麗的舞台，讓眾人為你的舞技心醉神迷！\r\n\r\n「《Just Dance 舞力全開 2023》TGS娛樂賽」就在「台北國際電玩展」，一起來秀吧！', 'Switch', ' 個人', '1724228645.jpg', '2023-02-05 00:00:00', '2023-02-12 00:00:00', '2023-02-19 13:30:00', 21, 1, '0000-00-00 00:00:00'),
(8, 'The Updraft W2 Powered by PLANET9', 'Valorant', 'The goal of this event is to help grow the community competitive scene of Southeast Asia. While big teams have their sights for VCT, teams that could not make the cut there but have the drive to compete and hone their skills have an option by joining Updraft.', 'PC', '團隊', '1724228675.jpg', '2023-02-03 11:00:00', '2023-02-10 23:00:00', '2023-02-14 20:00:00', 16, 1, '0000-00-00 00:00:00'),
(9, 'AfreecaTV Mastiff League Season 2 - Apex Legends', 'Apex Legends', '這次比賽由AfreecaTV主辦的Apex Legends比賽，名為 AfreecaTV Mastiff League。簡稱「AML」。本季為第二季，勝出的隊伍會成為第三季的種子隊伍。\r\n\r\n「AfreecaTV」為韓國其中一個最大的網絡直播平台，目前服務已推廣至台灣、泰國、越南、日本及美國香港分公司於2015年成立，主以合作及提供免費直播支援來推廣服務。\r\n\r\n近年亦於台港澳地區舉辦不同的電競比賽，推廣地區電競發展。', 'PC', '團隊', '1724228692.jpg', '2022-12-19 12:00:00', '2022-12-30 12:00:00', '2023-01-03 18:00:00', 80, 1, '0000-00-00 00:00:00'),
(10, 'The Updraft W3 Powered by PLANET9', 'Valorant', 'The goal of this event is to help grow the community competitive scene of Southeast Asia. While big teams have their sights for VCT, teams that could not make the cut there but have the drive to compete and hone their skills have an option by joining Updraft.', 'PC', '團隊', '1724228714.jpg', '2023-02-18 11:00:00', '2023-02-25 23:00:00', '2023-02-28 20:00:00', 16, 1, '0000-00-00 00:00:00'),
(11, 'AOPEN x Fire Legend Championships (素人組)', 'Apex Legends', '希望藉由本次競賽促進台灣APEX社群ARENA高端對局的交流，並同時讓平常在段位賽中廝殺的玩家們能有機會來一場刺激的競賽。期望本次活動能讓社群更有參與感並吸引更多玩家加入APEX，建立友善的交流遊玩環境。', 'PC', '團隊', '1724228729.jpg', '2022-12-15 00:00:00', '2023-01-01 00:00:00', '2023-01-08 14:00:00', 21, 1, '0000-00-00 00:00:00'),
(12, '爬瓦盃 POWER CHAMPION CUP👀', 'Valorant', '爬瓦盃是由輔仁大學資管系與DINOGAMEHOUSE一起合辦的特戰英豪比賽\r\n\r\n滿15歲以上便可報名(未滿18歲需填家長同意書)\r\n\r\n報名費以人頭計算，一個人頭一百元，一隊最少5人最多7人\r\n\r\n賽程從3/2開始到3/19，最多招收64隊\r\n\r\n本平台只用於賽事報名與流程進行\r\n\r\n主要溝通平台為DISCORD\r\n\r\n只要參賽就能參與參賽隊伍專屬的抽獎\r\n\r\n快來報名拔༼ つ ◕_◕ ༽つ', 'PC', '團隊', '1724228750.png', '2023-02-16 00:00:00', '2023-03-01 00:00:00', '2023-03-02 00:00:00', 64, 1, '0000-00-00 00:00:00'),
(13, 'The Updraft W4 Powered by PLANET9', 'Valorant', 'The goal of this event is to help grow the community competitive scene of Southeast Asia. While big teams have their sights for VCT, teams that could not make the cut there but have the drive to compete and hone their skills have an option by joining Updraft.', 'PC', '團隊', '1724228773.jpg', '2023-03-04 11:00:00', '2023-03-11 23:00:00', '2023-03-14 20:00:00', 16, 1, '0000-00-00 00:00:00'),
(14, 'AfreecaTV TFT The Chosen One TW&HK (S11) 海選賽', 'Teamfight Tactics', '這次比賽為《AfreecaTV TFT The Chosen One TW&HK》的海選賽 (第一階段及第二階段)，第一階段及第二階段預賽將於 4月1日14:00 順序進行，積分最高的首8名選手將晉級到邀請賽部份，除爭奪豐富的獎金獎品外，更可獲得進入SEA預賽的資格，挑戰世界賽(Set Championship)的機會！\r\n\r\n\r\n\r\n邀請選手將於稍後公布，希望和戰棋界名人較量的話，就不能錯過我們的系列賽啊！', 'Mobile/PC', '個人', '1724228790.png', '2023-03-29 12:45:00', '2023-04-01 18:00:00', '2023-04-01 18:15:00', 24, 1, '0000-00-00 00:00:00'),
(15, '(小組A)AfreecaTV TFT The Chosen One TW&HK (S11) 邀請賽', 'Teamfight Tactics', '這次比賽為《AfreecaTV TFT The Chosen One TW&HK》的海選賽 (第一階段及第二階段)，第一階段及第二階段預賽將於 4月1日14:00 順序進行，積分最高的首8名選手將晉級到邀請賽部份，除爭奪豐富的獎金獎品外，更可獲得進入SEA預賽的資格，挑戰世界賽(Set Championship)的機會！\r\n\r\n\r\n\r\n邀請選手將於稍後公布，希望和戰棋界名人較量的話，就不能錯過我們的系列賽啊！', 'Mobile/PC', '個人', '1724228816.png', '2023-04-03 13:15:00', '2023-04-03 14:00:00', '2023-04-03 19:30:00', 8, 1, '0000-00-00 00:00:00'),
